mapper.select:
select 

,

from __MigrationHistory
where MigrationId = #{migrationid,jdbcType=NVARCHAR}
  and ContextKey = #{contextkey,jdbcType=NVARCHAR}

mapper.delete:
delete from __MigrationHistory
where MigrationId = #{migrationid,jdbcType=NVARCHAR}
  and ContextKey = #{contextkey,jdbcType=NVARCHAR}

mapper.update:
update __MigrationHistory


    ProductVersion = #{productversion,jdbcType=NVARCHAR},


    Model = #{model,jdbcType=VARBINARY},


where MigrationId = #{migrationid,jdbcType=NVARCHAR}
  and ContextKey = #{contextkey,jdbcType=NVARCHAR}

update __MigrationHistory
set ProductVersion = #{productversion,jdbcType=NVARCHAR},
  Model = #{model,jdbcType=VARBINARY}
where MigrationId = #{migrationid,jdbcType=NVARCHAR}
  and ContextKey = #{contextkey,jdbcType=NVARCHAR}

update __MigrationHistory
set ProductVersion = #{productversion,jdbcType=NVARCHAR}
where MigrationId = #{migrationid,jdbcType=NVARCHAR}
  and ContextKey = #{contextkey,jdbcType=NVARCHAR}
